={{ 
  $('Clean Fields').all()
    .filter(x => x.json.recipient_first_name === $json.recipient_first_name)
    .map( x => x.json
      .removeField("recipient")
      .removeField("recipient_first_name")
      .removeField("recipient_last_name")
    )
    .concat(Array.isArray($json.array) ? $json.array.filter(x => typeof x === 'object' && x) : [])
    .filter((item, index, self) =>
      index === self.findIndex(t => JSON.stringify(t) === JSON.stringify(item))
    )
    .map(item =>
      Object.entries(item)
        .map(([k, v]) => `${k}: ${v}`)
        .join('\n')
        .replaceAll(": 0.00", ": Unknown")
    )
    .join('\n\n')
}}

